echo "Sample,TotalSeqs,Chr1,Chr2,Chr3,Chr4,Chr5,mitochondria,chloroplast" > total_aligned_by_chromosome.csv;  for bam in  /tsl/scratch/shrestha/pingtao/dingp_Ath_ATACseq_2019_1/wt*_sorted.bam /tsl/scratch/shrestha/pingtao/dingp_Ath_ATACseq_2019_1/seti[^gh]*_sorted.bam   ; do base=$(basename $bam | sed 's/_sorted.bam//;s/_/ /g' | awk 'BEGIN{OFS="_"}{$NF="";  print $0}' | sed 's/_$//');  total=$(grep ^Total /tsl/scratch/shrestha/pingtao/dingp_Ath_ATACseq_2019_1/results/fastqc/$base/$base/*R1_fastqc/fastqc_data.txt | awk '{print $3}'); echo $base $total  $(cat ${bam}.chr.count.txt | awk -v total=$total '{print $2/2}') ; done | sed 's/ /,/g' >> total_aligned_by_chromosome.csv
